package com.algorithms.lesson_1;import java.util.Scanner;/** *  Вычислить сумму двух входных целых чисел, лежащих в отрезке от нуля до десяти. * * @author makson * */class Main {    public static void main(String[] args) {        final Scanner scanner = new Scanner(System.in);        final int a = scanner.nextInt();        final int b = scanner.nextInt();        final int sum = a + b;        System.out.println(sum);        scanner.close();    }}