package com.stepic.lesson_2;import java.util.Scanner;/** * Дано целое число 1≤n≤40, необходимо вычислить n-е число Фибоначчи (напомним, что F0=0, F1=1 и Fn=Fn−1+Fn−2 при n≥2). * * @author makson * */public class Fibonacci {      public static void main(String[] args) {        final Scanner scanner = new Scanner(System.in);          int n = scanner.nextInt();          if(!(n >= 1) || !(n <= 40)) throw new RuntimeException();          int[] arr = new int[n + 1];          arr[0] = 0;          arr[1] = 1;          for (int i = 2; i <= n; i++) {              arr[i] = (arr[i - 2] + arr[i - 1]);          }          System.out.print(arr[n]);        scanner.close();    }}