package com.stepic.lesson_2;import java.util.Scanner;/** * Дано целое число 1≤n≤40, необходимо вычислить n-е число Фибоначчи (напомним, что F0=0, F1=1 и Fn=Fn−1+Fn−2 при n≥2). * * @author makson * */public class Fibonacci {      public static void main(String[] args) {        final Scanner scanner = new Scanner(System.in);        final int n = scanner.nextInt();        int f_1 = 1;        int f_2 = 0;        int current = 0;        for (int i = 0; i < n; i++) {            current = f_1 + f_2;            f_2 = f_1;            f_1 = current;        }        System.out.println(f_2);        scanner.close();    }}