package com.stepic.lesson_3;import java.util.Scanner;/** * Дано целое число 1≤n≤10 в 7, найти последнюю цифру n-го числа Фибоначчи. 0≤a,b≤9 — последние цифры чисел Fi и Fi+1 соответственно, то (a+b)mod10 — последняя цифра числа Fi+2. * * @author makson * */public class Fibonacci {      public static void main(String[] args) {        final Scanner scanner = new Scanner(System.in);          int n = scanner.nextInt();          if(!(n >= 1) || !(n <= 1e+7)) throw new RuntimeException();          int[] arr = new int[n + 1];          arr[0] = 0;          arr[1] = 1;          for (int i = 2; i <= n; i++) {              arr[i] = (arr[i - 2] + arr[i - 1]) % 10;          }          System.out.print(arr[n]);        scanner.close();    }}